mapper.select:
SELECT s.id as id, s.t_s_id as subjectTypeId, s.t_c_id as categoryId, s.name as name, s.image_url as imageUrl, s.remark as remark, ct.difficult as difficulty, st.name as subjectTypeName, sc.name as categoryName, s.status as status, s.updated_time as updatedTime, s.org_id as orgId, s.company_id as companyId, s.version as version FROM t_subject s left outer join t_subject_type st on s.t_s_id = st.id left outer join t_category sc on s.t_c_id = sc.id left outer join t_comb_exam_config_item ct on ct.t_s_id = st.id
AND s.name = #{name}
AND st.name = #{subjectTypeName}
AND sc.name = #{categoryName}

SELECT c.id
from t_category c

  c.name = #{categoryName}

SELECT st.id
from t_subject_type st

  st.name = #{SubjectTypeName}

SELECT s.id as id, s.t_s_id as subjectTypeId, s.t_c_id as categoryId, s.name as name, ct.difficult as difficulty, st.name as subjectTypeName, sc.name as categoryName, s.status as status, s.updated_time as updatedTime, s.org_id as orgId, s.company_id as companyId, s.version as version FROM t_subject s left outer join t_subject_type st on s.t_s_id = st.id left outer join t_category sc on s.t_c_id = sc.id left outer join t_comb_exam_config_item ct on ct.t_s_id = st.id
AND ct.difficult = #{difficulty}
AND st.name = #{subjectTypeName}
AND sc.name = #{categoryName}
LIMIT #{num}
